async activeModuleContent => {
  const contentType = activeModuleContent.type;
  let content;
  const moduleContent = await fetchOneCourseModuleContent.run({
    id: activeModuleContent.id
  });
  if (contentType === 'Reading Material') {
    content = await fetchReadingContent.run({
      id: activeModuleContent.id
    });
  }
  if (contentType === 'Video') {
    content = await fetchVideoContent.run({
      id: activeModuleContent.id
    });
  }
  if (contentType === 'Quiz') {
    const quiz = await fetchQuizContent.run({
      id: activeModuleContent.id
    });
    const quizQuestions = await fetchQuizQuestions.run({
      quizId: quiz[0].id
    });
    content = [{
      ...quiz[0],
      questions: quizQuestions
    }];
  }
  utils.activeModuleContent = {
    activeModuleContent: moduleContent && moduleContent.length > 0 ? moduleContent.map(c => {
      return {
        id: c.id,
        type: c.content_type,
        name: c.title,
        markComplete: c.mark_complete,
        comment: c.comment
      };
    })[0] : undefined,
    content: content.length > 0 ? content[0] : undefined
  };
}

